import React, { useContext, useState, useEffect } from 'react';
import './myOrders.css';
import { StoreContext } from '../../context/StoreContext'; // Ensure you're importing from the correct path
import axios from 'axios';
import {assets} from '../../assets/assets'
import { or } from 'firebase/firestore/lite';

const MyOrders = () => {
  const { url,token } = useContext(StoreContext);
  const [data, setData] = useState([]);


  const fetchOrders = async () => {
    try {
      // Send POST request to fetch orders
      const response = await axios.post(url + "/api/order/userorders", {}, {
        headers: { token }
      });

      // Check if the request was successful
      if (response.data.success) {
        // Set the orders data and log the response data
        setData(response.data.data);

      } else {
        // Handle case where the server returned an error in the response
        console.error("Failed to fetch orders:", response.data.message || "Unknown error");
      }
    } catch (error) {
      // Catch any error that occurs during the request
      console.error("Error fetching orders:", error.response ? error.response.data : error.message);
    }
  };



  useEffect(() => {
    if (token) {
      fetchOrders();
    }
  }, [token]);

  return (
    <div className="my-orders">
      <h2>my orders</h2>
      <div className="container">
        {data.map((order, index) => (
          <div key={index} className="order">
            <div className="order-image">
              <img src={assets.parcel_icon} alt="" />
            </div>
            <div className="order-details">
              <p>Order ID: {order._id}</p>
              <p>Order Date: {order.orderDate.toDateString()}</p>
              <p>Total Price: {order.totalPrice} USD</p>
              <p>Status: {order.status}</p>
            </div>
          </div>
        ))}
      </div>

    </div>
  );
};

export default MyOrders;
