import React, { useContext, useState, useEffect } from 'react';
import './myOrders.css';
import { StoreContext } from '../../context/StoreContext'; // Ensure you're importing from the correct path
import axios from 'axios';

const MyOrders = () => {
  const { url, token } = useContext(StoreContext);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await axios.post(url + "/api/order/userorders", {}, { headers: { token } });
      if (response.data.success) {
        setData(response.data.data);
        console.log(response.data.data);
      } else {
        setError("Failed to fetch orders");
      }
    } catch (error) {
      console.error("Error fetching orders:", error);
      setError("Failed to fetch orders");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (token) {
      fetchOrders();
    }
  }, [token]);

  if (loading) {
    return <div>Loading orders...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="my-orders-container">
      {data.length === 0 ? (
        <div>No orders found</div>
      ) : (
        <div className="orders-list">
          {data.map((order) => (
            <div key={order._id} className="order-item">
              <h3>Order ID: {order._id}</h3>
              <p>Amount: {order.amount} ETB</p>
              <p>Address: {order.address}</p>
              <p>Status: {order.payment ? "Paid" : "Pending"}</p>
              <div className="order-items">
                {order.items.map((item) => (
                  <div key={item._id} className="order-item-details">
                    <p>{item.name} x {item.quantity}</p>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MyOrders;
