import React, { useState } from 'react';
import './Home.css';
import Header from '../../components/navbar/Header/Header';
import ExploreMenu from '../../components/ExploreMenu/ExploreMenu';
import FoodDisplay from '../../components/FoodDisplay/FoodDisplay';
import AppDownload from '../../components/AppDwonload/AppDownload';

function Home() {

  const [category, setCategory] = useState("All");

  return (
    <div>
      <Header />

      {/* About Dawit Section */}
      <section className="about-dawit">
        <div className="about-container">
          <h2 className="about-heading">Meet Dawit, Your Food Expert!</h2>
          <p className="about-description">
            Hi, I'm Dawit! I am a passionate food creator, dedicated to bringing unique and delicious flavors to 
            every dish I make. Cooking is not just my job—it's my love, my art, and my way of connecting with 
            people. My mission is to create flavorful and memorable meals that bring joy to your table.
          </p>
          <p className="about-description">
            My culinary journey began many years ago, and since then, I've been exploring new tastes, recipes, 
            and techniques. I believe that food is a universal language, and I’m excited to share my passion with 
            you. From comforting meals to adventurous new dishes, I promise something for every palate. Join me, 
            and let’s explore the world of food together!
          </p>
        </div>
      </section>

      <ExploreMenu category={category} setCategory={setCategory} />
      <FoodDisplay category={category} />
      <AppDownload />
    </div>
  );
}

export default Home;
