import React, { useEffect, useState } from 'react'
import './LoginPopup.css'
import {assets} from '../../assets/assets'

function LoginPopup({setShowLogin}) {
    const [currentState,setCurrentState] = useState("Login")
    const [data ,setdata] = useState({
        email: '',
        password: '',
        name: '',
    })
    const handleSubmit = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setdata({...data,[name]:value})

    }
    useEffect = (event) => {
        event.preventDefault();
        if(currentState === "Login"){
            // login
        }
        else{
            // register
        }
    }
  return (
    <div className='login-popup'>
        <form className='login-popup-container'>
            <div className='login-popup-title'>
                <h2>{currentState}</h2>
                <img onClick={()=>setShowLogin(false)} src={assets.cross_icon} alt="" />
            </div>
            <div className='login-popup-input'> 
                {currentState==="Login"?<></>:<input name='name' onChange={handleSubmit()} value={data.name} type="text"  placeholder='Your name 'required/>}

                <input name='email' onChange={handleSubmit()} value={data.email} type="email" placeholder='Your email ' required/>
                <input name='password' onChange={handleSubmit} value={data.password} type="password" placeholder='Your password ' required/>
            </div>
            <div className='login-popup-button'>
                <button type='submit'>{currentState === "Sign Up"?"Create account":"Login"}</button>
            </div>
            <div className='login-popup-condition'>
                <input type="checkbox"  required/>
                <p>
                    I agree to the Terms and Conditions and Privacy Policy. 
                </p>
            </div>
            {
               currentState === "Login"
               ?
               <p>Create new account? <span onClick={()=>setCurrentState("Sign Up")}>Click here</span></p>
               :
               <p>Already have an account? <span onClick={()=>setCurrentState("Login")}>login here</span> </p>
            }


        </form>

    </div>
  )
}

export default LoginPopup
