import React, { useContext, useState, useEffect } from 'react';
import './myOrders.css';
import { StoreContext } from '../../context/StoreContext'; // Ensure you're importing from the correct path
import axios from 'axios';

const MyOrders = () => {
  const { url,token } = useContext(StoreContext);
  const [data, setData] = useState([]);


  const fetchOrders = async () => {
    try {
      // Send a POST request to fetch user orders with the token in headers
      const response = await axios.post(url + "/api/order/userorders", {}, {
        headers: { token }
      });

      if (response.data.success) {
        // Set the fetched orders data
        setData(response.data.data);
        console.log(response.data.data);  // Log the orders data
      } else {
        console.error("Failed to fetch orders:", response.data.message);
      }
    } catch (error) {
      if (error.response) {
        // If the error has a response (i.e., error is from the server)
        console.error("Error fetching orders:", error.response.data);
      } else {
        // If the error does not have a response
        console.error("Error fetching orders:", error.message);
      }
    }
  };

  useEffect(() => {
    if (token) {
      fetchOrders();
    }
  }, [token]);

  return (
    <div className="my-orders-container">

    </div>
  );
};

export default MyOrders;
